import React, { useState } from "react";
import '../styles/forget.css';
import { useNavigate } from "react-router-dom";
require('dotenv').config();


const Forget = () => {
    const [email, setemail] = useState("")

    let navigate = useNavigate()

    const forgot = (e) => {
        e.preventDefault();
        let useremail = document.getElementById('error')
        let mail = email
        if (mail==="") {
            useremail.innerText= 'please input your email'
        } else {
            fetch(`${process.env.REACT_APP_URL}/fogetpassword`, {
                method: "POST",
                crossDomain: true,
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json",
                    "Access-Control-Allow-Origin": "*",
                },
                body: JSON.stringify({
                    Useremail: mail,
                }),
                })
                .then(res => res.json())
                .then(data=> {
                    if (data.status === 'ok') {
                        alert(data.message)
                        navigate("/Verify")
                    } else {
                        alert(data.message)
                    }
                }) 
        }
       
    }


    return(
        <>
             <div className="forget">
                <div className="forget_form">
                    <div className="background_forget_gradient">
                        <form>
                            <input type="text" placeholder="Email" onChange={(e)=>{setemail(e.target.value)}} id="input"/>
                            <p id="error"></p>
                            <button type="btn" id="forget_button" onClick={forgot}>Send OTP</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}


export default Forget
