import React, { useState } from 'react';

const Signup = () => {
    const [email, setemail] = useState('');
    const [password, setpassword] = useState('');
    const [cpassword, setcpassword] = useState('');

    const submit = () => {
        let isValid = true;
        let emailPattern = /^\w+([.-]?\w+)*@\w+([.-]?\w+)*(.\w{2,3})+$/;
        let passwordPattern = /^(?=.*\d)(?=.*[a-z])(?=.*[A-Z]).{8,}$/;

        if (email === '') {
            isValid = false;
            document.getElementById('error1').innerHTML = 'Please enter email';
        } else if (!emailPattern.test(email)) {
            isValid = false;
            document.getElementById('error1').innerHTML = 'Please enter valid email';
        }

        if (password === '') {
            isValid = false;
            document.getElementById('error2').innerHTML = 'Please enter password';
        } else if (!passwordPattern.test(password)) {
            isValid = false;
            document.getElementById('error2').innerHTML = 'Please enter valid password';
        }

        if (cpassword === '') {
            isValid = false;
            document.getElementById('error3').innerHTML = 'Please confirm password';
        } else if (cpassword !== password) {
            isValid = false;
            document.getElementById('error3').innerHTML = 'Passwords do not match';
        }

        if (isValid) {
            // You can send the form data to the server here
            alert('Sign up successful');
        }
    };

    return (
        <>
            <div className="sign-up">
                <div className="siderbar">
                    {/* Your siderbar content here */}
                </div>
                <div className="form_signup">
                    <nav>
                        <p>Already have an account?</p>
                        <a href="/login"><button>SIGN IN</button></a>
                    </nav>
                    <div className="form">
                        <h1>Sign Up</h1>
                        <form>
                            <input type="email" placeholder="Email" id="email" onChange={(e) => { setemail(e.target.value) }} />
                            <p id="error1"></p>
                            <input type="password" placeholder="Password" id="password" onChange={(e) => { setpassword(e.target.value) }} />
                            <p id="error2"></p>
                            <input type="password" placeholder="Confirm password" id="c-password" onChange={(e) => { setcpassword(e.target.value) }} />
                            <p id="error3"></p>
                            <input type="button" value={"SIGN UP"} id="btn1" onClick={submit} />
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
}

export default Signup;
