import React, { useEffect, useState } from "react";
import axios from "axios"
import { useParams, Link } from 'react-router-dom';
import Side from "../side/side";
import '../styles/home.css';
require('dotenv').config();

const Results = () => {
    let params = useParams();
    let Useremail = params.Useremail
    let Title = params.Title
    const [position, setposition] = useState("")
    const [candidate, setcandidate] = useState("")
    const [info, setinfo] = useState("")

    useEffect(()=>{
        axios.get(`${process.env.REACT_APP_URL}/position_title/${Useremail}/${Title}`)
        .then((res)=> {
            setposition(res.data)
        })
    })
    const result = (Useremail, Title, position) => {
        axios.get(`${process.env.REACT_APP_URL}/getcandidateinfo/${Useremail}/${Title}/${position}`)
        .then((res)=> {
            setcandidate(res.data)
        })


        if (candidate.status === 'ok') {
            axios.get(`${process.env.REACT_APP_URL}/results/${Useremail}/${Title}/${position}/${candidate.data.candidateName}`)
            .then((res)=> {
                setinfo(res.d)
            })
        }
    }
    return(
        <>
        <Side/>
        <div className="home">
        <h1 id="Position_title">{Title}</h1>
                <div className="home_body">
                    {Array.isArray(position.data) && position.data.length > 0 ? (
                        position.data.map((title) => (
                                <div className="Add_new">
                                    <p id="poll">{title.position}</p>
                                    <button type="button" onClick={()=> result(title.Useremail, title.Title, title.position)}>View the number of vote</button>
                                </div>
                        ))
                    ) : (
                       <h1 id="world">Wait for the result data</h1> 
                    )}
                </div>
            </div>
        </>
    )
}

export default Results
