import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Voter_platform_vote = () => {
    let params = useParams()
    let Useremail = params.Useremail
    let Title = params.Title
    let _id = params._id
    const [Position, setPosition] = useState("")
    const [User_file, setUser_file] = useState("")
    const [candidate, setcandidate] = useState("")
    useEffect(()=>{
        axios.get(`http://localhost:2000/position_title/${Useremail}/${Title}`)
        .then((res)=> {
            setPosition(res.data)
        })
    },[])

    useEffect(()=>{
        axios.get(`http://localhost:2000/user_info/${_id}`)
        .then((res)=> {
            setUser_file(res.data)
        })
    },[])

    {/*useEffect(()=>{
        axios.get(`http://localhost:2000/getcandidateinfo/${Useremail}/${Title}/${position}`)
        .then((res)=> {
            setcandidate(res.data)
        })
    })*/}
    const project_impossible = () => {
        for (let index = 0; index < Position.length; index++) {
            axios.get(`http://localhost:2000/getcandidateinfo/${Useremail}/${Title}/${position}`)
        .then((res)=> {
            setcandidate(res.data)
        })
            
        }
    }
    const button = () => {
        if (candidate.data) {
            return(
                <>
                    <button type="button" id="mission_impossible_dead-button">Next</button>
                </>
            )
        }
    }
    const project_impossible_part2 = () => {
        if (candidate.data) {
            return(
                <>
            {Array.isArray(candidate.data) && candidate.data.length > 0 ? (
                candidate.data.map((title) => (
                   <p id="position">{title.candidateName}</p>
                ))
            ) : (
               <></>
            )}
            </>
            )
        }
    }
    return(
        <>
            <div className="sider-form_positions">
            {Array.isArray(Position.data) && Position.data.length > 0 ? (
                        Position.data.map((title) => (
                           <p id="position">{title.position}</p>
                        ))
                    ) : (
                       <></>
                    )}
            </div>
            <div className="result_voting-section">
                <h1 id="result_Title">{Title}</h1>
                {project_impossible_part2(Position)}
                {project_impossible(Position)}
                {button(Position)}
            </div>
        </>
    )
}

export default Voter_platform_vote
