import React, { useState } from "react";
import '../styles/verify.css'
import { useParams } from "react-router-dom";



const guest_verify = () => {
    let params = useParams
    let Useremail = params.Useremail
    const [email, setemail] = useState("")
    const [otp, setotp] = use
    const verify = (e) => {
        e.preventDefault();
        let  useremail= document.getElementById('error')
        let lotp = document.getElementById('error1')
        let Password = document.getElementById('error2')

            if (email.trim().length === 0) {
                useremail.innerText = 'input a vaild email'
            } else {
                useremail.innerText = ''
                if (email.match("^([a-zA-Z0-9]+)@gmail.com")===null) {
                    useremail.innerText = 'This is how your email should be like example@gmail.com'
                } else{
                    if (otp.trim().length === 0) {
                        lotp.innerText = "Please input a vaild otp"
                    } else {
                        if (PhoneNumber.trim().length === 0) {
                            Password.innerText = "Please input your Phone number"
                        } else {
                                fetch("http://localhost:2000/verifyotp", {
                            method: "POST",
                            crossDomain: true,
                            headers: {
                                "Content-Type": "application/json",
                                Accept: "application/json",
                                "Access-Control-Allow-Origin": "*",
                            },
                            body: JSON.stringify({
                                email,
                                otp,
                                PhoneNumber,
                                Useremail
                            }),
                            })
                            .then(res => res.json())
                            .then(data => {
                                if(data.status === 'ok'){
                                    alert(data.message)
                                    navigate("/")
                                } else{
                                    alert(data.message)
                                }
                            })
                        }
                    }
                }
            }
    }

    const forgot = (e) => {
        e.preventDefault();
        let useremail = document.getElementById('error')
        let mail = email
        if (mail==="") {
            useremail.innerText= 'please input your email'
        } else {
            fetch('http://localhost:2000/sendotp', {
                method: "POST",
                crossDomain: true,
                headers: {
                    "Content-Type": "application/json",
                    Accept: "application/json",
                    "Access-Control-Allow-Origin": "*",
                },
                body: JSON.stringify({
                    email: mail,
                }),
                })
                .then(res => res.json())
                .then(data=> {
                    if (data.status === 'ok') {
                        alert(data.message)
                    } else {
                        alert(data.message)
                    }
                }) 
        }
    }


    return(
        <>
            <div className="verify">
                <div className="verify_form">
                    <div className="background_gradient">
                        <form>
                            <input type="text" placeholder="email" onChange={(e)=>{setemail(e.target.value)}}/>
                            <p id="error"></p>
                            <input type="tel" placeholder="OTP" onChange={(e)=>{setotp(e.target.value)}}/>
                            <p id="error1"></p>
                            <input type="password" placeholder="PhoneNumber" onChange={(e)=>{setPhoneNumber(e.target.value)}} id="npassword"/>
                            <p id="error2"></p>
                            <input type="btn" value={"Verify"} id="verify_button" onClick={verify}/>
                            <p id="resend">your otp code will expire after 2mins, 
                            <br/>click here to resend the otp</p>
                            <br/>
                            <button type="btn" id="resend_button" onClick={forgot}>resend</button>
                        </form>
                    </div>
                </div>
            </div>
        </>
    )
} 


export default guest_verify
